mapper.update:
update board

	boardTitle=#{boardTitle},
	boardContents=#{boardContents},
	code=#{code}

where boardNum=#{boardNum}

update board
adminDelete=#{adminDelete}, deleteDate=sysdate
where boardNum=#{boardNum}

mapper.select:
SELECT *
	FROM (
	select row_number() over(order by replyNum desc) as rnum,t.*
	from noticeReply t
	where boardNum=#{boardNum}
	)
